class Solution {
public:
    #define ll long long int 
    const ll mod = 1e9 + 7;

    ll min(ll a, ll b) {
        if(a <= b) return a;
        else return b;
    }

    ll add(ll a, ll b)
    {
        a %= mod; b %= mod;
        ll ans = (a + b) % mod;
        return ans % mod;
    }

