    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        ll dp[2001][1001];
        memset(dp, 0, sizeof(dp));

        for(ll i=0; i<=nums[n-1]; i++) {
            dp[n-1][i] = 1;
        }

        for(ll j=999; j>=0; j--) {
            dp[n-1][j] += dp[n-1][j+1];
        }

        for(ll i=n-2; i>=0; i--)
        {
            for(ll j=0; j<=nums[i]; j++) {
                ll a = j; ll b = nums[i] - j;
                ll range = nums[i+1] - nums[i] + j;
                ll d = max(j, range);
                dp[i][j] = add(dp[i][j], dp[i+1][d]);

                // for(ll k=max(j , range); k<1001; k++) {
                //     dp[i][j] += dp[i+1][k];
                // }
            }

            for(ll j=999; j>=0; j--) {
                dp[i][j] = add(dp[i][j], dp[i][j+1]);
            }
        }

        return dp[0][0];

