    int countOfPairs(vector<int>& nums) {
        //int mx=nums[0];
        int ans=0;
        int mod=(int)1e9+7;
        int n=nums.size();
        vector<vector<int>> dp(n+1,vector<int> (1000+1,0));
                vector<vector<int>> preDp(n+1,vector<int> (1000+1,0));


        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1;
            preDp[0][i]=1;
            if(i){
                preDp[0][i]=(preDp[0][i]%mod)+(preDp[0][i-1])%mod;
            }
        }

        for(int i=1;i<n;i++){
            int ele=nums[i];
            //int res=0;
            for(int j=0;j<=ele;j++){

                int Counter=nums[i]-j;
                int pres=j;
               // int prev=pres-Counter;
                int res=0;
                int prev=nums[i-1]-pres;
                int bottom=prev;
                if(prev>=Counter){
                    prev=j;

                }
                else{
                    int diff=Counter-bottom;
                    prev=j-diff;

                }

                if(prev>=0){
                    res=res%mod;
                    int add=(preDp[i-1][prev])%mod;
                    res=(res+add)%mod;


                }
                dp[i][j]=(res)%mod;
            }


            for(int j=0;j<=ele;j++){
                 if(j){
                preDp[i][j]=(dp[i][j]%mod)+(preDp[i][j-1]%mod);
                     preDp[i][j]=(preDp[i][j])%mod;
            }
                else{
                    preDp[i][j]=(dp[i][j])%mod;
                }

            }
        }
        //cout << "hey "<<dp[2][5]<<endl;
        for(int i=0;i<=nums[nums.size()-1];i++){
            ans=ans%mod;
            int curr=dp[n-1][i]%mod;
            ans=(ans+curr)%mod;
        }
        return ans;
