class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [
            [0 for _ in range(max(nums)+1)] for _ in nums 
        ]

        for i in range(nums[0], -1, -1):
            dp[0][i] = 1
            if i < nums[0]:
                dp[0][i] += dp[0][i+1]

        # for line in dp:
        #     print(line)

        for i in range(1, len(nums)):
            for x1 in range(nums[i], -1, -1):
                x2_upper_bound = x1-min(0, nums[i]-nums[i-1])

                if x2_upper_bound < 0: continue

                dp[i][x1] += dp[i-1][x2_upper_bound]
                if x1 < nums[i]:
                    dp[i][x1] += dp[i][x1+1]

        # print()
        # for line in dp:
        #     print(line)

        return dp[-1][0] % (7 + 10 ** 9)
