class Solution {
public:
	const long long MOD = 1e9 + 7;
	void compute(vector<int>& sum, vector<int>& dp) {
		for (int i = 0; i < dp.size(); i++)sum[i + 1] = (sum[i] + dp[i]) % MOD;
	}
	int countOfPairs(vector<int>& nums) {
		int n = nums.size();
		nums.insert(nums.begin(), 1000);
		// at index i, arr1[i] = j
		vector<vector<int>>dp(n+2);
		vector<vector<int>>sum(n + 2);
		for (int i = 0; i <= n; i++)dp[i].resize(nums[i] + 1), sum[i].resize(nums[i] + 2);
		dp[0][0] = 1;
		compute(sum[0], dp[0]);
		for (int i = 1; i <= n; i++) {
			for (int k = 0; k <= nums[i]; k++) {
				int arr2 = nums[i] - k;
				int l = 0, r = k;
				if (arr2 > nums[i - 1] - r)r -= (arr2 - (nums[i - 1] - r));
				if (l <= r)dp[i][k] = sum[i - 1][r + 1] - sum[i - 1][l];
			}
			compute(sum[i], dp[i]);
		}
		long long ret = 0;
		for (int i = 0; i < min((int)dp[n].size(), 1001); i++)ret = (ret + dp[n][i]) % MOD;
		return ret;
	}
};
