using ll = long long;
constexpr ll mod = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int size = nums.size();
        vector<vector<ll>> dp(size + 1, vector<ll>(1002));
        vector<vector<ll>> p(size + 1, vector<ll>(1002));
        int num = nums[0];
        for (int a = 0; a <= num; ++a) {
            if (num - a >= 0)
                dp[0][a] = 1;
        }
        for (int a = 0; a <= 1000; ++a)
            p[0][a + 1] = p[0][a] + dp[0][a];

        int num0;
        int num1;
        int b;
        int bb;
        int mn;
        for (int i = 1; i < size; ++i) {
            num0 = nums[i];
            num1 = nums[i - 1];
            mn = min(num0, num1);
            for (int a1 = 0, a0 = num0 - mn; mn >= 0; --mn, a1++, a0++) {
                dp[i][a0] += p[i - 1][a1 + 1];
                dp[i][a0] %= mod;
            }
            for (int a = 0; a <= 1000; ++a)
                p[i][a + 1] = p[i][a] + dp[i][a];
        }
        ll ans = 0;
        for (int a = 0; a <= 1000; ++a) {
            ans += dp[size - 1][a];
            ans %= mod;
        }
        return ans;
    }
};
