class Solution {
public:


    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size()+1,vector<int>(1002,0));
        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1;
        }
        for(int i=1;i<nums.size();i++){
            vector<int> pref(nums[i-1]+1);
            pref[0]=dp[i-1][0]%1000000007;
            for(int j=1;j<=nums[i-1];j++){
                pref[j]=(pref[j-1]%1000000007+dp[i-1][j]%1000000007)%1000000007;
            }
            for(int j=0;j<=nums[i];j++){
                int ind=nums[i-1]-nums[i]+j;
                if(ind>j){
                    ind=j;
                }
                    if(ind>=0 && ind<pref.size()){
                        dp[i][j]=(dp[i][j]%1000000007+pref[ind]%1000000007)%1000000007;
                    }
                    // cout<<i<<" "<<j<<" "<<dp[i][j]<<endl;
            }
        }
        int s=0;
        for(int i=0;i<=nums[nums.size()-1];i++){
            s=(s+dp[nums.size()-1][i]%1000000007)%1000000007;
        }
        return s;
    }
};
