#define ll long long
#define SIZ 1000

const ll mod = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<ll> dp(SIZ+1, 0);
        for (int x = 0; x <= SIZ; ++x) {
            if (nums[0]-x >= 0)
                dp[x] = 1;
        }

        vector<ll> sm(SIZ+1, 0);
        sm[0] = dp[0];
        for (int i = 1; i <= SIZ; ++i)
            sm[i] = (sm[i-1] + dp[i]) % mod;

        for (int i = 1; i < n; ++i) {
            // cout << "For nums[" << i << "]: " << nums[i] << endl;
            vector<ll> ndp(SIZ+1, 0);
            for (int val1 = 0; val1 <= SIZ; ++val1) {
                int val2 = nums[i] - val1;
                if (val2 < 0)
                    continue;
                // we can use val2 ... SIZ from prv state,
                // this corresponds to prv_val1 = [ nums[i-1]-val2 , nums[i-1]-SIZ ]
                int mx_val = min(val1, nums[i-1]-val2);
                ndp[val1] = (mx_val < 0) ? 0 : sm[mx_val];

                // cout << "val1: " << val1 << ", val2: " << val2 << ", mx_val: " << mx_val << ", dp: " << ndp[val1] << endl;
            }

            sm[0] = ndp[0];
            for (int i = 1; i <= SIZ; ++i)
                sm[i] = (sm[i-1] + ndp[i]) % mod;
        }

        return sm[SIZ];
    }
};
