/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
    let prev = Array(1001).fill(0);
    let next = Array(1001).fill(0);
    let MOD = Math.pow(10, 9) + 7;

    prev[0] = 1;
    for (let x = 1; x <= nums[0]; x++) {
        prev[x] = prev[x-1] + 1;
    }


    for (let i = 1; i < nums.length; i++) {
        if (nums[i] > nums[i-1]) {
            for (let x = nums[i] - nums[i-1]; x <= nums[i]; x++) {
                let y = nums[i-1] - nums[i] + x;
                next[x] = next[x-1] + prev[y];
                next[x] %= MOD;
            }
        } else {
            next[0] = prev[0];
            for (let x = 1; x <= nums[i]; x++) {
                next[x] = next[x-1] + prev[x];
                next[x] %= MOD;
            }
        }

        prev = next;
        next = Array(1001).fill(0);
    }

    return prev[nums.at(-1)];
};
