typedef long long ll;
const ll MOD = (ll)(1e9 + 7);
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> ans(nums[0] + 1, 1LL);
        for (int i = 1; i < nums.size(); i++) {
            vector<ll> cs = ans;
            for (int j = 1; j < ans.size(); j++)
                cs[j] = (cs[j] + cs[j - 1]) % MOD;
            vector<ll> tmp(nums[i] + 1, 0LL);
            for (int j = 0; j <= nums[i]; j++) {
                int idx = min(j, nums[i - 1] - nums[i] + j);
                if (idx >= 0)
                    tmp[j] = cs[idx];
            }
            swap(ans, tmp);
        }
        ll res = 0LL;
        for (int i = 0; i < ans.size(); i++)
            res = (res + ans[i]) % MOD;
        return (int)res;
    }
};
