class Solution {
public:
    int f[2001][1001];
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9 + 7;
        int n = nums.size();
        int maxNum = *max_element(nums.begin(), nums.end());
        for(int i = 0; i < n; i++)
            for(int j = 0; j < maxNum; j++)
                    f[i][j] = 0;


        for(int i = 0; i < n; i++)
        {
            if(i == 0)
                for(int j = 0; j <= nums[i]; j++)
                    f[i][j]= 1;
            else
            {
                int tmp = 0;
                int cnt = 0;
                for(int j = 0; j <= nums[i]; j++)
                {
                    if(nums[i - 1] - nums[i] + j >= 0)
                    {
                        tmp =  (tmp + f[i - 1][cnt++] % mod) % mod;
                        f[i][j] = (f[i][j] + tmp) % mod;
                    }
                }
            }
        }
        cout << f[1][1] << ' ' << f[1][2] << ' ' << f[1][3];
        int result = 0;
        for (int j = 0; j <= nums[n - 1]; ++j) {
            result = (result + f[n - 1][j]) % mod;
        }
        return result;
    }
};
