class Solution {
public:
	long long dp[2001][1001];
	int N;

	int countOfPairs(vector<int> &nums) {
		N = nums.size();
		vector<int> a, b;
		for (auto x : nums)
			a.push_back(x);
		b.resize(nums.size());
		for (int i = nums.size() - 1; i > 0; i--) {
			if (a[i - 1] > a[i]) {
				int d = a[i - 1] - a[i];
				a[i - 1] -= d;
				b[i - 1] += d;
			}
		}
		for (int i = nums.size() - 1; i > 0; i--) {
			if (b[i - 1] < b[i]) {
				int d = b[i] - b[i - 1];
				a[i - 1] -= d;
				b[i - 1] += d;
			}
		}
        for (int i = 0; i < N; i++) {
            if (a[i] < 0 || b[i] < 0)
                return 0;
        }
		dp[N - 1][a.back()] = 1;
		for (int n = a.back() - 1; n >= 0; n--) {
			dp[N - 1][n] = dp[N - 1][n + 1] + 1;
		}
		for (int i = N - 2; i >= 0; i--) {
			dp[i][a[i]] = dp[i + 1][max(a[i], nums[i + 1] - (nums[i] - a[i]))];
			for (int n = a[i] - 1; n >= 0; n--) {
				dp[i][n] = (dp[i][n + 1] + dp[i + 1][max(n, nums[i + 1] - (nums[i] - n))]) % 1000000007;
			}
		}
		return dp[0][0];
	}
};
