class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        pairs = [0] * 1001 # pairs[i] for arr1
        for i in range(nums[0]+1):
            pairs[i] = 1
        prefix = self.getprefix(pairs)
        for i in range(1, len(nums)):
            s = nums[i]
            pre = nums[i-1]
            tmp = [0] * 1001
            for a in range(s+1):
                if prefix[a] == 0:
                    continue
                b = s - a
                maxPreA = min(nums[i-1] - b, a)
                if maxPreA < 0:
                    continue
                tmp[a] = prefix[maxPreA] % 1000000007
            pairs = tmp
            prefix = self.getprefix(pairs)
        return sum(pairs) % 1000000007

    def getprefix(self, a):
        b = [0] * len(a)
        b[0] = a[0]
        for i in range(1, len(a)):
            b[i] = b[i-1] + a[i]
        return b
