class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 +7
        k = 1000
        n = len(nums)
        cum = [0]*(k+1)
        # cum = list(range(1, k+2))[::-1]
        prev = None
        for i in range(n-1, -1, -1):
            x = nums[i]
            if i == n-1:
                for j in range(x+1):
                    cum[j] = 1
                for j in range(1, len(cum)):
                    cum[j] = cum[j] + cum[j-1]
            else:
                cur = [0] * (k+1)
                pi = min(x, prev) 
                for j in range(x, -1, -1):
                    if pi < 0:
                        break
                    # print(x, j, pi)
                    cur[j] = cum[pi]
                    pi -= 1
                for j in range(1, len(cur)):
                    cur[j] = (cur[j] + cur[j-1]) % MOD
                cum = cur
            prev = x
            # print(f"{cum=}")
        return cum[-1] % MOD
