class Solution {
public:
    vector<vector<int>> dp;
    int mod=(int)1e9+7;

    int countOfPairs(vector<int>& nums) {
        int mx=*max_element(nums.begin(),nums.end());
        int n=nums.size();
        dp.resize(nums.size(),vector<int>(mx+1,0));



        for(int j=0;j<=nums[0];j++){
            dp[0][j]=1;
        }
        vector<int> pfx(mx+1,0);
        pfx=dp[0];
        for(int i=1;i<n;i++){

            for(int j=1;j<=mx;j++){
                pfx[j]=(pfx[j-1]+pfx[j])%mod;
            }
            for(int j=0;j<=nums[i];j++){

                int endj=min(nums[i-1]-nums[i]+j,j);

                if(endj>=0 && endj<=mx)
                dp[i][j]=pfx[endj];
                //cout<<stj<<" ";
            }
            pfx=dp[i];
            //cout<<endl;

        }

        int sum=0;
        for(int k=0;k<=nums[n-1];k++)
            sum=(sum+dp[n-1][k])%(mod);

        return sum;
    }
};
