class Solution {
    public int countOfPairs(int[] nums) {
        int dp[][] = new int[nums.length][1002], result = 0, curr = 0;
        dp[0][nums[0] + 1] = 1000000006;
        for (int i = dp[0][0] = 1; i < nums.length; i++) {
            for (int j = 0, k = 0; j <= nums[i - 1]; j++) {
                k = (k + dp[i - 1][j]) % 1000000007;
                if (j + Math.max(0, nums[i] - nums[i - 1]) <= nums[i]) {
                    dp[i][j + Math.max(0, nums[i] - nums[i - 1])] = (dp[i][j + Math.max(0, nums[i] - nums[i - 1])] + k) % 1000000007;
                    dp[i][nums[i] + 1] = (dp[i][nums[i] + 1] - k + 1000000007) % 1000000007;
                }
            }
        }
        for (int i : dp[nums.length - 1]) {
            result = (result + (curr = (curr + i) % 1000000007)) % 1000000007;
        }
        return result;
    }
}
