    int countOfPairs(vector<int>& num) {
        n=num.size();
        int mx=0;
        for(int i=0;i<n;i++){
            nums[i]=num[i];
            mx=max(mx,nums[i]);
        }
        mx++;
        vector<vector<int>> pref,dp;
        dp.resize(n+1,vector<int>(mx+2));
        pref.resize(n+1,vector<int>(mx+2));
        for(int i=0;i<mx;i++){
            int val=((i<=nums[0])?1:0);
            dp[0][i]=val;
            pref[0][i]=val; 
            if(i){
                pref[0][i]=(pref[0][i]%modm + pref[0][i-1]%modm)%modm;
            }
        }
        for(int i =1;i<(int)num.size();i++){
            for(int j=0;j<mx;j++){
                if(nums[i]>=j)
                {
                    int curb=nums[i]-j;
                    if(nums[i-1]<curb){dp[i][j]=0;}
                    else{
                        dp[i][j]=(pref[i-1][min(j,nums[i-1]-curb)]%modm);//-pref[i-1][nums[i-1]-curb]%modm)%modm;
                        // cout<<"#"<<i<<" "<<j<<" "<<pref[i-1][nums[i-1]-curb]<<"\n";
                    }
                }else {dp[i][j]=0;}
                if(j) pref[i][j] = (pref[i][j-1]%modm + dp[i][j]%modm)%modm;
                else pref[i][j]=dp[i][j];
                //cout<<i<<" "<<j<<" "<<dp[i][j]<<"\n";
            }
        }
        int res=0; 
        for(int j=0;j<=nums[n-1];j++){
            res = (res%modm+dp[n-1][j]%modm)%modm;

        }
        return res;
    }
};
