class Solution {
    int mod = 1e9+7;
public:
    int f(int i, int k, vector<int>& nums, vector<vector<int>>& dp){
        if(i==nums.size()) return 1;
        if(dp[i][k]!=-1) return dp[i][k];
        int ans=0;
        for(int j=max(k, nums[i]-nums[i-1]+k);j<=nums[i];j++){
            ans = ((ans%mod)+(f(i+1, j, nums, dp)%mod))%mod;
        }
        return dp[i][k] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(), i, j, ans=0;
        if(n==1) return nums[0]+1;
        vector<vector<int>> dp(n, vector<int>(1001, 0));
        // for(i=0;i<=nums[0];i++){
        //     ans = ((ans%mod)+(f(1, i, nums, dp)%mod))%mod;
        // }

        for(i=0;i<1001;i++){
            if(max(i, nums[n-1]-nums[n-2]+i) <= nums[n-1]) dp[n-1][i] = nums[n-1] - max(i, nums[n-1]-nums[n-2]+i) + 1;
            else dp[n-1][i] = 0;
        }


        for(i=n-2;i>=1;i--){
            vector<int> sp(1001);
            sp[0] = dp[i+1][0];
            for(j=1;j<1001;j++) sp[j] = ((sp[j-1]%mod) + (dp[i+1][j]%mod))%mod;
            for(j=0;j<1001;j++){
                if(max(j, nums[i]-nums[i-1]+j) > nums[i]) continue;
                dp[i][j] = sp[nums[i]]%mod;
                if(max(j, nums[i]-nums[i-1]+j)>0) dp[i][j] -= sp[max(j, nums[i]-nums[i-1]+j)-1]%mod;
                if(dp[i][j]<0) dp[i][j] += mod;
            }
        }

        for(i=0;i<=nums[0];i++){
            ans = ((ans%mod)+(dp[1][i]%mod))%mod;
        }

        return ans;
    }
};
