class Solution {
    public int countOfPairs(int[] nums) {
        long[][] dp = new long[nums.length][1001];
        for (int i = 0; i <= nums[0]; i++) dp[0][i] = 1;

        for (int i = 1; i < dp.length; i++) {
            int minInc = Math.max(0, nums[i] - nums[i-1]);
            int curr = 0;
            for (int j = 0; j <= nums[i]; j++) {
                if (j - minInc >= 0) curr += dp[i-1][j - minInc];
                curr %= 1000000007;

                dp[i][j] = curr;
            }
        }

        /*
        for (int r = 0; r < dp.length; r++) {
            for (int c = 0; c < dp[r].length; c++) {
                System.out.print(dp[r][c] + " ");
            }
            System.out.println();
        }*/

        long res = 0;
        for (int col = 0; col <= nums[nums.length-1]; col++) {
            res += dp[nums.length-1][col];
            res %= 1000000007;
        }
        return (int)res;
    }
}
