class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        temp = [nums[0]]
        temp2 =[0]
        for i in range(1,len(nums)):
            if nums[i]<nums[i-1]:
                dist = nums[i-1]-nums[i]
                temp.append(max(temp[-1]-dist,0))
                temp2.append(nums[i]-temp[-1])
                if temp2[-1]<temp2[-2]:
                    return 0
            else:
                temp.append(temp[-1])
                temp2.append(nums[i]-temp[-1])
        #print(temp)

        dp1 = [[None for j in range(len(nums))] for i in range(temp[0]+1)]
        dp2 = [[None for j in range(len(nums))] for i in range(temp[0]+1)]
        #print(dp1)
        for j in range(len(nums)-1,-1,-1):
            count =0
            for i in range(temp[0]+1):
                if j == len(nums)-1:
                    candidate = temp[0]-i
                    #print(candidate)
                    if temp[j] >= candidate:
                        dp1[i][j]=1


                elif j < len(nums)-1:
                    dist = temp[j]-temp[j+1]
                    if i+dist <= len(dp2)-1:
                        dp1[i][j] = dp2[i+dist][j+1]



            for i in range(temp[0],-1,-1):
                if dp1[i][j] != None:
                    count += dp1[i][j]
                dp2[i][j] = count


        #print(dp1)
        #print(dp2)

        return dp2[0][0]%(10**9+7)
