class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        total = 0
        p = 10 ** 9 + 7
        size = len(nums)

        dp = [[0] * 1001 for _ in range(size)]
        for i in range(0, nums[0] + 1):
            dp[0][i] = 1

        for i, num in enumerate(nums[1:], 1):
            pre = dp[i - 1][::]
            for j in range(1, 1001):
                pre[j] = (pre[j] + pre[j - 1]) % p


            for x in range(0, num + 1):
                y = num - x
                ma = min(nums[i - 1], x)
                mi = nums[i - 1] - y

                if 0 <= min(mi, ma):
                    dp[i][x] = pre[min(mi, ma)]


        return sum(dp[size - 1]) % p
