    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(2001,vector<int>(1001,0));
        vector<int> sum(1001,0);
        for(int i=0;i<=1000;i++){
            dp[n][i]=1;
        }
        for(int i=nums[n-1];i>=0;i--){
            if(i==1000){
                sum[i]=dp[n][i];
            }
            else
                sum[i]=(sum[i+1]+dp[n][i])%MOD;
        }
        for(int i=n-1;i>0;i--){
            for(int prev=0;prev<=nums[i-1];prev++){
                int j=0;
                j=max(j,nums[i]-nums[i-1]+prev);
                j=max(j,prev);
                if(j>1000)
                    continue;
                //cout<<i<<" "<<prev<<" "<<j<<" "<<endl;
                dp[i][prev]=sum[j];
            }
            for(int j=nums[i];j>=0;j--){
                if(j==1000){
                    sum[j]=dp[i][j];
                }
                else
                    sum[j]=(sum[j+1]+dp[i][j])%MOD;
            }
        }
        return sum[0];
    }
