class Solution {
    private:
    int add(int x,int y){
        const int M = 1e9+7;
        return ((x%M) + (y%M))%M;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int M = 1e9+7;
        int dp[n+1][1001];
        int pref[1001];
        for(int i =0;i<=n;i++)
            for(int j= 0;j<=1000;j++)
                dp[i][j] = 0;
        dp[0][0] = 1;
        pref[0] = 1;
        for(int i = 1;i<=nums[0];i++) dp[0][i] = 1;
        for(int i = 1;i<=1000;i++) pref[i] = add(pref[i-1],dp[0][i]);

        int ans = 0;
        for(int i =1;i<n;i++){
            for(int j = 0;j<=nums[i];j++){
                int limit = min(j, nums[i-1]-nums[i]+j);
                // dp[i][j] = summation dp[i-1][k]
                if(limit >= 0){
                    dp[i][j] = add(pref[limit],0);
                }
            }
            pref[0] = add(dp[i][0], 0);
            for(int j = 1;j<=nums[i];j++) pref[j] = add(pref[j-1],dp[i][j]);
        }
        for(int i = 0;i<=1000;i++){
            ans = ((dp[n-1][i]%M) + (ans%M))%M;
        }
        return ans;

    }
};
