using ll=long long;
class Solution {
public:
    const int mod=1e9+7;
    long long recur(int i,int mx,vector<vector<long long>>&dp,vector<int>&a){
        if (i==a.size()){
            return 1;
        }
        if (mx!=-1 && dp[i][mx]!=-1){
            return dp[i][mx];
        }
        ll ans=0;
        for (int j=mx;j<=a[i];j++){
            if (i==0){
                ans+=recur(i+1,j,dp,a);
                ans%=mod;
            }
            else{
                if (a[i]-j>=0 && (a[i-1]-mx)>=(a[i]-j)){
                    ans+=recur(i+1,j,dp,a);
                    ans%=mod;
                }
            }
        }
        if (mx!=-1){
            dp[i][mx]=ans;
        }
        return ans;

    }
    int countOfPairs(vector<int>& a) {
        int n=a.size();
        if (n==1){
            return a[0]+1;
        }
        ll mod=1e9+7;
        vector<vector<ll>>dp(n+10,vector<ll>(1010,0));
        for (int i=0;i<n;i++){
            ll sum=0;
            for (int j=0;j<=a[i];j++){
                if (i==0){
                    dp[i+1][j]+=1;
                    dp[i+1][j]%=mod;
                    dp[i+1][a[i]+1]-=1;
                }
                else{
                    ll lst=-a[i-1]+a[i]+j;
                    sum+=dp[i][j];
                    sum%=mod;
                    if (j>=lst){
                        lst=j;
                    }
                    // cout << i << " " << j << " " << lst << endl;
                    // cout << dp[i][j] << " " << sum << endl;
                    if (lst>=0 && lst<=1000){
                    dp[i+1][lst]+=sum;
                    dp[i+1][lst]%=mod;
                    }
                    // dp[i+1][a[i]+1]-=sum;
                    dp[i][j]=sum;
                    // cout << "added " << i+1 << " " << lst << endl;
                }
            }

        }
        ll ans=0;
        for (int i=0;i<=a[n-1];i++){
            ans+=dp[n][i];
            ans%=mod;
        }
        return ans;


    }
};
