class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;

        vector<vector<int>> dp(n, vector<int>(1001, 0));

        for (int v = 0; v <= nums[0]; ++v) {
            dp[0][v] = 1;
        }

        for (int i = 1; i < n; ++i) {
            vector<int> prefix(1001, 0);

            for (int v = 0; v <= nums[i]; ++v) {
                prefix[v] = (prefix[v] + dp[i-1][v]) % MOD;
                if (v > 0) {
                    prefix[v] = (prefix[v] + prefix[v-1]) % MOD;
                }
            }

            for (int v = 0; v <= nums[i]; ++v) {
                if(min(v, v+nums[i-1]-nums[i]) < 0) continue;
                dp[i][v] = prefix[min(v, v+nums[i-1]-nums[i])];
            }
        }

        int ans = 0;
        for (int v = 0; v <= nums[n-1]; ++v) {
            ans = (ans + dp[n-1][v]) % MOD;
        }

        return ans;
    }
};
