class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_val = max(nums) + 1
        dp = [[0] * max_val for _ in range(n + 1)]

        for i in range(nums[0] + 1):
            dp[1][i] = 1
        prefix_arr = []
        prefix_arr.append(dp[1][0])
        for i in range(1, len(dp[1])):
            prefix_arr.append(prefix_arr[-1] + dp[1][i])
        # print(prefix_arr)
        for i in range(2, n + 1):
            for j in range(nums[i-1] + 1):
                curr_num = nums[i-1]
                prev_num = nums[i-2]
                if curr_num <= prev_num:
                    # up to index
                    dp[i][j] = prefix_arr[j]
                else:
                    diff = curr_num - prev_num
                    stop_pt = j - diff
                    if stop_pt < 0:
                        dp[i][j] = 0
                    else:
                        dp[i][j] = prefix_arr[stop_pt]
                    # do something
            row = dp[i]
            prefix_arr = []
            prefix_arr.append(row[0])
            for i in range(1, len(row)):
                prefix_arr.append(prefix_arr[-1] + row[i])
            # print(prefix_arr)
        answer = 0
        for i in range(nums[-1] + 1):
            answer = (answer + dp[n][i]) % MOD
        # print(dp)

        return answer
