class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int dp[2000][1001],k=1e9+7;
        memset(dp,0,sizeof(dp));
        for(int i=0;i<=1000;i++)
        {
            if(i<=nums[0])dp[0][i]=1;
            if(i-1>=0)dp[0][i]+=dp[0][i-1];
        }
        for(int i=1;i<nums.size();i++)
        {
            for(int j=0;j<=nums[i];j++)
            {
                int ww=nums[i]-j;
                int g=nums[i-1]-ww;
                if(g<0)continue;
                int w=-1;
                if(g>j)w=j;
                else if(g<=j)
                w=g;
                dp[i][j]=dp[i-1][w];
            }
            for(int j=1;j<=1000;j++)
                dp[i][j]=(dp[i][j]+dp[i][j-1])%k;
        }
        return dp[nums.size()-1][1000];
    }
};
