class Solution {
public:
    int countOfPairs(vector<int>& nums) {
      int mod_p = 1e9+7;
      vector<int> dp(1001, 0);
      int n = nums.size();
      for (int now = 0; now <= nums[0]; now++) {
        dp[now] = 1;
      }
      for (int i = 1; i < n; i++) {
        vector<int> tmp(dp);
        for (int now = 0; now <= 1000; now++) dp[now] = 0;
        int prefix_sum = 0;
        for (int now = 0; now <= nums[i]; now++) {
            int last = min(now, now - nums[i] + nums[i-1]);
            if (last < 0) continue;
          //for (int last = 0; last <= min(now, now - nums[i] + nums[i-1]); last++) {
            prefix_sum += tmp[last];
            prefix_sum %= mod_p;
            dp[now] = prefix_sum;
          //}
        }
        //for (int now = 0; now <= 50; now++) cout << dp[now] << " ";
        //cout << endl;
      }
      //for (int now = 0; now <= 50; now++) cout << dp[now] << " ";
      //cout << endl;
      int ret = 0;
      for (int now = 0; now <= 1000; now++)  {
        ret += dp[now];
        ret = ret % mod_p;
      }
      return ret;

      }
};
