class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [0] * (max(nums)+1)
        pre_sum = nums[0]
        for i in range(nums[0]+1):
            dp[i] += 1

        for i in nums[1:]:
            temp = [0] * len(dp)
            prefix = [0]
            for j in dp:
                prefix.append(prefix[-1]+j)
            for nums1 in range(max(0,i-pre_sum),i+1):
                temp[nums1] = prefix[min(pre_sum-i+nums1,nums1)+1]
                temp[nums1] = temp[nums1]%(10**9+7)
            pre_sum = i    
            dp = temp[:]
        return sum(dp)%(10**9+7)
