class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[0] * (max(nums) + 1) for _ in range(len(nums))]
        for i in range(nums[-1] + 1):
            dp[-1][i] = 1
        for i in range(len(nums) - 2, -1, -1):
            m, n = nums[i], nums[i+1]
            pre = [0] * (max(nums) + 1)
            pre[0] = dp[i+1][0]
            for j in range(1, m + 1):
                pre[j] = pre[j-1] + dp[i+1][j]
            for j in range(m + 1):
                if n-(m-j) >= 0:
                    dp[i][j] = int(pre[min(n-(m-j), j)] % int(1e9 + 7))
        return int(sum(dp[0]) % int(1e9 + 7))
