class Solution {
public:
    int M;
    int countOfPairs(vector<int>& nums) {
        M = 1e9+7;

        int n = nums.size(); 
        vector<int> prefix(1001,0);
        // int prev_sum = nums[n-1];
        vector<vector<int>> dp(n,vector<int>(1001,0));
        for(int ai = 0;ai<=nums[n-1];ai++){
            dp[n-1][ai] = 1;
        };
        int prev = 0;
        for(int ai = 0;ai<=1000;ai++){
            prefix[ai] = (prev%M + dp[n-1][ai]%M)%M;
            prev = prefix[ai];
            //stores the number of subarrays starting from 0 to a[i] for the i+1 th index; 
        };

        for(int i =n-2;i>=0;i--){
            // cout<<"\n--------------\nindex is "<<i<<" \n";
            for(int ai = 0;ai<=nums[i];ai++){
                int bi = nums[i]-ai;
                int start = max(ai,nums[i+1]-bi);
                int sum = 0;
                //sum stores the number of subarrays from start to 1001 for the i+1th index
                // for(int s = start;s<=1000;s++){
                //     sum = (sum%M + dp[i+1][s]%M)%M;
                // };
                int prev = (start == 0) ? 0 : prefix[start-1];
                int till = prefix[1000];
                int also = (till%M -prev%M)%M;
                // cout<<"also is "<<also<<"\n";
                // cout<<"sum is "<<sum<<"\n";
                //we have to find the number of sequences that are less than 
                dp[i][ai] = also;
            };
            int prev = 0;
            for(int ai = 0;ai<=1000;ai++){
                prefix[ai] = (prev%M + dp[i][ai]%M)%M;
                prev = prefix[ai];
                //stores the number of subarrays starting from 0 to a[i] for the i+1 th index; 
            };

            // cout<<"prefix array is \n";
            // for(int i = 0;i<5;i++){
            //     cout<<prefix[i]<<" ";
            // };
            // cout<<endl;
            // cout<<" and dp array is \n";
            // for(int it = 0;it<5;it++){
            //     cout<<dp[i][it]<<" ";
            // };
            // cout<<endl;



        };
        int ans = prefix[1000];
        // for(int i = 0;i<=50;i++){
        //     ans =(ans%M + dp[0][i]%M)%M;
        // };
        return (M+ans)%M;

    }
};
