typedef long long ll;
ll M = 1e9+7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        vector <vector <ll>> dp(n,vector <ll> (1002));
        for(ll i=0;i<=nums[n-1];i++)
        {
            dp[n-1][i] = 1;
        }
        for(ll i=1001;i>0;i--)
        {
            dp[n-1][i-1]+=dp[n-1][i];
            dp[n-1][i-1]%=M;
        }
        for(ll i=n-2;i>=0;i--)
        {
            for(ll j=0;j<=nums[i];j++)
            {
                dp[i][j] =  dp[i+1][max(j,nums[i+1]-nums[i]+j)] ;
                // cout<<dp[i][j]<<endl;
            }
            for(ll j = 1001;j>0;j--)
            {
                dp[i][j-1]+=dp[i][j];
                dp[i][j-1]%=M;
            }
        }

        return dp[0][0];
    }
};
