class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9+7;
        const int MAX_V = 1000;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(MAX_V + 1, 0));

        // Initialize dp for the first element
        for (int i = 0; i <= min(nums[0], MAX_V); i++) {
            dp[0][i] = 1;
        }

        // Fill the dp table
        for (int i = 1; i < n; ++i) {
            int sum = 0;
            for (int j = 0; j <= min(nums[i], MAX_V); ++j) { 
                // First condition
                if (nums[i-1] - j >= nums[i] - j && nums[i-1] - j >= 0) {
                    sum = (sum + dp[i-1][j]) % MOD;
                }
                // Second condition
                else if (nums[i] - j >= 0 && nums[i-1] - (nums[i] - j) >= 0 && nums[i-1] - (nums[i] - j) <= MAX_V) {
                    sum = (sum + dp[i-1][nums[i-1] - (nums[i] - j)]) % MOD;
                }

                dp[i][j] = (dp[i][j] + sum) % MOD;
            }
        }

        // Sum all dp[n-1][j] for all j to get the final answer
        int result = 0;
        for (int j = 0; j <= min(nums[n-1], MAX_V); ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
