class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int sz = nums.size();
        vector<vector<int>> dp(sz+1,vector<int>(1010));
        dp[sz][1]=1;
        int mod = 1e9+7;
        int ps = 0;
        for(int i=sz-1;i>=0;i--)
        {
            int sub= 0;
            if(i<sz-1 && nums[i]>nums[i+1]) sub=nums[i]-nums[i+1];
            ps +=sub;
            //cout<<sub<<endl;
            for(int j=0;j<=nums[i]-ps;j++) dp[i][j+1] = (dp[i+1][j+1]+dp[i][j])%mod;
            //for(auto e : dp[i]) cout<<e<<" ";
            //cout<<endl;
        }
        int r = 0;
        for(int i=1;i<=nums[0]+1;i++) r=(r+dp[0][i])%mod;
        return r;
    }
};
