const int N = 2010, MOD = 1e9 + 7;
long long dp[N][N];
long long p[N][N];

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        int mx = 0;
        for(int i : a) mx = max(mx, i);
        for(int i = 0; i < n; i++) {
            for(int j = 0; j <= mx; j++) {
                dp[i][j] = 0;
                p[i][j] = 0;
            }
        }

        for(int i = 0; i <= a[0]; i++) {
            dp[0][i] = 1;
            p[0][i] = 1;
            if(i) p[0][i] += p[0][i - 1];
        }


        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= a[i]; j++) {
                int com = a[i] - j;
                int L = min(a[i - 1], j); // 0 - leftbound
                int l = a[i - 1], r = l - L;
                if(com > l) continue; 
                dp[i][j] += p[i - 1][l - max(com, r)];
                dp[i][j] %= MOD;

            }

            for(int j = 0; j <= a[i]; j++) {
                p[i][j] = dp[i][j];
                if(j) {
                    p[i][j] += p[i][j - 1];
                    p[i][j] %= MOD;
                }
            }
        }


        long long ans = 0;
        for(int i = 0; i <= a.back(); i++) {
            ans += dp[n - 1][i];
            ans %= MOD;
        }

        return (int)(ans);
    }
};
