class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n, vector<long long>(1001, 0));
        vector<vector<long long>> prefix(n, vector<long long>(1001, 0));

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }
        for (int j = 0; j <= 1000; ++j) {
            prefix[0][j] = dp[0][j] + (j > 0 ? prefix[0][j - 1] : 0);
            prefix[0][j] %= MOD;
        }
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                int max_prev = min(j, nums[i-1] - (nums[i] - j));
                if (max_prev >= 0) {
                    dp[i][j] = prefix[i-1][max_prev];
                }
            }
            for (int j = 0; j <= 1000; ++j) {
                prefix[i][j] = dp[i][j] + (j > 0 ? prefix[i][j - 1] : 0);
                prefix[i][j] %= MOD;
            }
        }
        long long ans = 0;
        for (int j = 0; j <= 1000; ++j) {
            ans = (ans + dp[n-1][j]) % MOD;
        }

        return ans;
    }

};
