class Solution {
public:
    int count(int curr, int last, vector<int> &nums, vector<vector<int>> &dp){
        if(curr>=nums.size()){
            return 1;
        }
        if(last>nums[curr]){
            return 0 ;
        }
        if(dp[curr][last]!=-1){
            return dp[curr][last] ;
        }
        int last2 = 10000 ;
        if(curr>=1){
            last2 = nums[curr-1] - last ;
        }
        long long int s = 0LL ;
        int M = 1e9 + 7 ;
        int start = nums[curr]-last2 ;
        if(start<last){
            start = last ;
        }
        for(int i=start;i<=nums[curr];i++){
            s = s + 1LL*count(curr+1, i, nums, dp) ;
            s = s%M ;
        }
        return dp[curr][last]=s;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<long long int>> dp(nums.size()+1, vector<long long int>(1060, 0LL)) ;
        int M = 1e9 + 7 ;
        for(int i=0;i<dp[0].size();i++){
            int s = 0 ;
            if(i>=1){
                s = dp[nums.size()-1][i-1] ;
            }
            dp[nums.size()-1][i] = s ;
            if(i<=nums[nums.size()-1]){
                dp[nums.size()-1][i]++ ;
            }
        }
        for(int i=nums.size()-2;i>=0;i--){
            for(int j=0;j<dp[0].size();j++){
               int start = nums[i+1] - nums[i] + j ;
               if(start < j){
                 start = j ;
               }
               int end = nums[i+1] ;
               if(start>end){
                    break ;
               }
               long long int y = dp[i+1][end] ;
               long long int x = 0LL ;
               if(start>0 && start-1<dp[i+1].size()){
                    x = dp[i+1][start-1] ;
               }
               dp[i][j] = y - x ;
               if(dp[i][j]<0){
                dp[i][j] +=M ;
               }
               dp[i][j] %=M ;
            }
            if(i!=-1){
                for(int j=1;j<dp[0].size();j++){
                    dp[i][j] += dp[i][j-1] ;
                    dp[i][j] %=M ;
                }
            }
        }

        return dp[0][nums[0]] ;
    }
};
