class Solution {
public:
    int mod = 1e9+7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int memo[2010][1010]{};
        for (int i = n-1; i >= 0; i--) {
            for (int j = 0; j <= nums[i]; j++) {
                int &res = memo[i][j];
                if (i == nums.size()-1) {
                    res = 1;
                    if (i > 0 && j > 0) res = ((res + memo[i][j-1]) % mod + mod) % mod;
                    continue;
                }
                res = 0;
                // for (int k = j-min(0, (nums[i]-nums[i+1])); k <= nums[i+1]; k++) {
                //     res = (res + memo[i+1][k]) % mod;
                // }
                if (j-min(0, nums[i]-nums[i+1]) <= nums[i+1]) {
                    if (j-min(0, (nums[i]-nums[i+1])) == 0) {
                        res = memo[i+1][nums[i+1]];
                    } else {
                        res = (memo[i+1][nums[i+1]] - memo[i+1][j-min(0, (nums[i]-nums[i+1]))-1]) % mod;
                    }
                    res = (res + mod) % mod;
                }
                if (i > 0 && j > 0) res = ((res + memo[i][j-1]) % mod + mod) % mod; // prefix sum
            }
        }
        int res = 0;
        for (int i = 0; i <= nums[0]; i++) {
            res = (res + memo[0][i]) % mod;
        }
        return res;
    }
};
