class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int modulo = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n, vector<long long>(1005, 0));
        vector<long long> presum(1002, 0);
        for(int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1;
            presum[i+1] += presum[i] + dp[0][i];
        }
        for(int i = 1; i < n; ++i) {
            vector<long long>nxt_presum(1005,0);
            auto diff = max(0, nums[i]-nums[i-1]);
            for(int j = 0; j <= nums[i]; ++j) {
                if (j-diff < 0) dp[i][j] = 0;
                else dp[i][j] = presum[j-diff+1];
                nxt_presum[j+1] = (nxt_presum[j] + dp[i][j])%modulo;
            }
            swap(presum, nxt_presum);
        }
        long long ans = 0;
        for(int i = 0; i < 1005; ++i) {
            ans += dp[n-1][i];
            ans %= modulo;
        }
        return ans;
    }
};
