class Solution {
    int MOD = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();
        vector<vector<int>> f(n, vector<int>(1001));
        for(int i = 0; i <= nums[0]; ++i) f[0][i] = 1;
        for(int i = 1; i < n; ++i) {
            int t = max(0, nums[i] - nums[i - 1]);
            for(int k = t; k <= nums[i]; ++k) {
                if(k == t) f[i][k] = f[i - 1][0];
                else f[i][k] = (f[i][k - 1] + f[i - 1][k - t]) % MOD;

                // for(int j = 0; j <= min(k, k - nums[i] + nums[i - 1]); ++j) {
                //     f[i][k] = (f[i][k] + f[i - 1][j]) % MOD;
                // }
            }
        }
        int res = 0;
        for(int i = 0; i < 1001; ++i) {
            res = (res + f[n - 1][i]) % MOD;
        }
        return res;
    }
};
