#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size(),M=1e9+7;
        vector<int> v(n+1);
        v[0]=1000;
        for(ll i=1;i<=n;i++)v[i]=nums[i-1];
        vector<vector<ll>> dp(n+1,vector<ll>(1001,0));
        for(ll i=0;i<=1000;i++)
        {
            if(i<=v[1])dp[1][i]=1;
            if(i)dp[1][i]+=dp[1][i-1];
        }
        for(ll i=2;i<=n;i++)
        {
            for(ll j=0;j<=v[i];j++)
            {
                ll pr=v[i-1];
                ll cx=j,cy=v[i]-j;
                ll px=min(j,pr);
                px=min(px,pr-cy);
                if(px<=1000 && px>=0)dp[i][j]=(dp[i][j]+dp[i-1][px])%M;
            }
            for(ll j=1;j<=1000;j++)
            {
                dp[i][j]=(dp[i][j]+dp[i][j-1])%M;
            }
        }
        return dp[n][1000];
    }
};
