class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), m = 1000 + 1, mod = 1000000007;
        vector<vector<int>> dp(n, vector<int>(m + 1));
        vector<int> sum(m + 1);

        for (int j = 0; j < m; ++j) {
            if (j <= nums[0]) dp[0][j] = 1;
            sum[j + 1] = sum[j] + dp[0][j];
        }

        for (int i = 1; i < n; ++i) {
            int d = max(0, nums[i] - nums[i - 1]);
            vector<int> t(m + 1);

            for (int j = 0; j < m; ++j) {
                if (d <= j + 1 and j <= nums[i]) {
                    dp[i][j] = (dp[i][j] + sum[j - d + 1]) % mod;
                }
                t[j + 1] = (t[j] + dp[i][j]) % mod;
            }
            sum = move(t);
        }

        int ret = 0;
        for (int j = 0; j < m; ++j) {
            ret = (ret + dp[n - 1][j]) % mod;
        }

        return ret;
    }
};
