class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        max_val = max(nums)
        dp = [0] * (max_val + 1)
        mod = 10**9 + 7

        for i in range(nums[0] + 1):
            dp[i] = 1

        for i in range(1, n):
            new_dp = [0] * (max_val + 1)
            prefix_sum = [0] * (max_val + 2)

            for j in range(max_val + 1):
                prefix_sum[j + 1] = (prefix_sum[j] + dp[j]) % mod

            for j in range(nums[i] + 1):
                limit = min(j, nums[i-1] - (nums[i] - j))
                if limit >= 0:
                    new_dp[j] = (prefix_sum[limit + 1]) % mod

            dp = new_dp

        return sum(dp) % mod
