constexpr long long mod = 1000000007;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int ans = 0;
        vector<vector<long long>> dp(nums.size(), vector<long long>(1002, 0));
        for (long long i = 0; i <= nums[0]; ++i) dp[0][i] = 1;

        vector<long long> prefixDp(1002, 0);
        prefixDp[0] = dp[0][0];
        for (int i = 1; i < prefixDp.size(); ++i) prefixDp[i] = (prefixDp[i-1] + dp[0][i]) % mod;

        for (int i = 1; i < nums.size(); ++i)
        {
            long long diff = nums[i] - nums[i-1];
            vector<long long> buf(1002, 0);
            for (int j = 0; j <= nums[i]; ++j)
            {
                if (diff <= 0)
                {
                    // for (long long k = 0; k <= j; ++k) {
                    //     dp[i][j] += dp[i-1][k];
                    //     dp[i][j] %= mod;
                    // }
                    dp[i][j] = prefixDp[j];
                }
                else
                {
                    // for (long long k = 0; k <= j - diff; ++k) {
                    //     dp[i][j] += dp[i-1][k];
                    //     dp[i][j] %= mod;
                    // }
                    // cerr << j - diff << endl;
                    if (j - diff >= 0) dp[i][j] = prefixDp[j - diff];
                }
                if (j == 0) buf[j] = dp[i][j];
                else 
                {
                    // cerr << i << ", " << j << ", " << buf[j-1] << ", " << dp[i][j] << endl;
                    // cerr << j << ", " << buf[j-1] << ", " << dp[i][j] << endl;
                    buf[j] = (buf[j-1] + dp[i][j]) % mod;
                }
            }
            prefixDp = buf;
        }

        for (long long i : dp.back()) ans = (ans + i) % mod;

        return ans;

    }
};
