class Solution {
	private static final int MODULUS = (int) 1e9 + 7;

	private static int add(int x, int y) {
		return (x + y) % MODULUS;
	}

	public static int countOfPairs(int[] nums) {
		int m = 0;
		for (int x : nums)
			m = Math.max(m, x);
		int[] count0 = new int[m + 1];
		int[] count1 = new int[m + 1];
		count0[0] = 1;
		int p = m;
		for (int x : nums) {
			int i = Math.max(x - p, 0);
			int j = 0;
			int c = count0[0];
			Arrays.fill(count1, 0, i, 0);
			count1[i] = c;
			while (++i <= x)
				count1[i] = c = add(c, count0[++j]);
			Arrays.fill(count1, x + 1, m + 1, 0);
			int[] temp = count0;
			count0 = count1;
			count1 = temp;
			p = x;
		}
		int result = 0;
		for (int i = 0; i <= m; i++)
			result = add(result, count0[i]);
		return result;
	}
}
