#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int max1 = 1000;
        int n = nums.size();
        vector<int> dp(max1 + 1, 0);
        vector<int> min(n, 0);
        for (int i = 1; i < n; i++){
            min[i] = max(0,nums[i] - nums[i-1]);
        }

        for (int i = 0; i <= nums[0]; i++) {
            dp[i] = 1;
        }

        for (int i = 1; i < n; i++) {
            vector <int> p (max1+1,0);
            for (int j = min[i]; j < max1+1; j++) {
                p[j] = dp[j-min[i]] + (j > 0 ? p[j - 1] : 0);
                p[j] %= 1000000007;
            }

            for (int j = 0; j <= nums[i]; j++) {
                dp[j] = p[j];
            }
            for (int j = nums[i]+1; j < max1+1; j++) {
                dp[j] = 0;
            }
        }

        int res = 0;
        for (int i = 0; i <= nums[n - 1]; i++) {
            res = (res + dp[i]) % 1000000007;
        }

        return res;
    }
};
