class Solution {
    public int countOfPairs(int[] nums) {
        int[][] dp = new int[nums.length][1001];
        for (int i = 0; i<=nums[0];i++){
            dp[0][i]=i+1;
        }
        int M = 1_000_000_007;
        for (int i = 1; i< nums.length;i++){
            for (int n = 0; n<=nums[i];n++){
                int upperBound = Math.min(n, nums[i-1]-nums[i]+n);
                if (n>0){
                    dp[i][n]+=dp[i][n-1];
                }
                dp[i][n]+=upperBound>=0?dp[i-1][upperBound]:0;
                dp[i][n]%=M;
            }
        }
        return dp[nums.length-1][nums[nums.length-1]];
    }
}
