const int MOD = 1e9 + 7;
int add(int a, int b) {
    a += b;
    if (a >= MOD) a -= MOD;
    return a;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int N = 2000;

        vector dp(n + 1, vector(N + 1, 0));
        for (int j = 0; j <= nums[0]; j++)
            dp[0][j] = 1;

        for (int i = 1; i < n; i++) {
            vector<int> acc(N + 2, 0);
            for (int j = 0; j <= N; j++) {
                int bj = nums[i - 1] - j;
                int l = max(j, nums[i] - bj);
                int r = nums[i];
                if (l <= r) {
                    acc[l] = add(acc[l], dp[i - 1][j]);
                    acc[r + 1] = add(acc[r + 1], (MOD - dp[i - 1][j]) % MOD);
                }
            }
            int at = 0;
            for (int k = 0; k <= N; k++) {
                at = add(at, acc[k]);
                dp[i][k] = at;
            }
        }

        int ans = 0;
        for (int j = 0; j <= N; j++) {
            ans = add(ans, dp[n - 1][j]);
        }

        return ans;
    }
};
