const int mod =1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(1001,0));
        for(int i = 0;i<=nums[0];i++){
            dp[0][i] = nums[0] - i + 1;
        }
        for(int i = 1;i < n;i++){
            for(int j = 0;j <=nums[i];j++){
                int x = nums[i] - j;
                int k = max(j,nums[i-1]-x);
                dp[i][j] = dp[i-1][k];
                // for(int k = max(j,nums[i-1]-x);k <= nums[i-1];k++){
                //     dp[i][j] = (dp[i][j] + dp[i-1][k]) %mod;

                // }
            }
            for(int k = 1000;k>=1;k--){
                dp[i][k-1] = (dp[i][k-1] + dp[i][k])%mod;
            }
        }
        return dp[n-1][0];
    }
};
