class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(2001, 0));

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;  
        }

        for (int i = 1; i < n; ++i) {
            vector<int> new_dp(2001, 0);
            vector<int> prefix_sum(2001, 0);

            prefix_sum[0] = dp[i-1][0];
            for (int k = 1; k <= 2000; ++k) {
                prefix_sum[k] = (prefix_sum[k-1] + dp[i-1][k]) % MOD;
            }

            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_i = nums[i] - j;
                if (arr2_i <= nums[i-1]) {
                    int max_k = min(j, nums[i-1] - arr2_i);
                    new_dp[j] = prefix_sum[max_k];
                }
            }

            dp[i] = new_dp;
        }

        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
